#mobile-share-boxtop{position:fixed;top:0;padding:0;margin:0;width:100%;z-index:501}#mobile-sharebar-footertop{position:relative;top:0;padding:0;margin:0;width:100%;z-index:500}#mobile-share-boxbottom{position:fixed;bottom:0;left:0;padding:0;margin:0;width:100%;z-index:100000000;margin-left:0}#mobile-sharebar-footerbottom{position:relative;bottom:0;padding:0;margin:0;width:100%;z-index:500}ul#horizontal-list{list-style:none;padding:0;margin:0;width:100%;display:table}ul#horizontal-list li{padding:0;margin:0;width:calc(100%/3);display:inline-block}.knfb{background-color:#3b5998;padding:0;margin:0;width:100%;background-image:url(../images/facebook.png);background-size:contain;background-repeat:no-repeat;background-position:center}.kntw{background-color:#3eaefb;padding:0;margin:0;width:100%;background-image:url(../images/twitter.png);background-size:contain;background-repeat:no-repeat;background-position:center}.knws{background-color:#2ab200;padding:0;margin:0;width:100%;background-image:url(../images/whatsapp.png);background-size:contain;background-repeat:no-repeat;background-position:center}.share-icon{padding:0;border:0;margin:0;width:100%}.hyperlink{background-image:url(../images/blank.png);display:block;padding:0;border:0;margin:0;width:100%;text-indent:-9999px}@media screen and (min-width:980px){#mobile-share-boxtop{display:none}#mobile-sharebar-footertop{display:none}#mobile-share-boxbottom{display:none}#mobile-sharebar-footerbottom{display:none}}